mapper.insert:
insert into 

notice(title, content, noticedate) 

values( #{title}, #{content}, #{noticeDate}  );

mapper.select:
select *

from notice

where noticeidx = #{noticeIdx} and title = #{title};

mapper.update:
update notice

set title = #{title}, content = #{content}, noticedate =#{noticeDate}, noticemodify = #{noticeModify}

where noticeidx = #{noticeIdx}

mapper.select:
select *

from notice

order by noticedate;

select count(*)

from member

where social = #{social};

select *

from shop




			and #{rating} <= rating and rating <= #{rating}+1 ; 



			and #{rating} <= rating and rating < #{rating}+1 ;

select * 
from member
where role = #{role}
order by name, role asc;

select *
from shop
where owneridx = #{owneridx}
order by shopname asc;

select *
from item
where shopidx = #{shopidx}
order by itemname asc;

  		select *
  		from shop
		order by shopname asc;

select i.*, sum(case when r.confirm <> 'false' then r.number else 0 end) as reservation_count from item as i
left join reservation as r
on i.itemidx = r.itemidx
where i.shopidx = #{shopidx}
group by i.itemidx
order by i.itemname asc;
