mapper.insert:
insert into 

notice(title, content, noticedate) 

values( #{title}, #{content}, #{noticeDate}  );

mapper.select:
select *

from notice

where noticeidx = #{noticeIdx} and title = #{title};

mapper.update:
update notice

set title = #{title}, content = #{content}, noticedate =#{noticeDate}, noticemodify = #{noticeModify}

where noticeidx = #{noticeIdx}

mapper.select:
select *

from notice

order by noticedate;

select count(*)

from member

where social = #{social};

select *

from shop




			and #{rating} <= rating and rating <= #{rating}+1 ; 



			and #{rating} <= rating and rating < #{rating}+1 ;

select * 
from member
where role = #{role}
order by name, role asc;

select *
from reservation
where memberidx = #{memberIdx} and confirm = 'false';
